DELIMITER //

CREATE OR REPLACE VIEW `view_readtable_ds_renderer_stylesheet` AS select `ds_renderer_stylesheet`.`group` AS `group`,`ds_renderer_stylesheet`.`classname` AS `classname`,concat('.',`ds_renderer_stylesheet`.`classname`,'{ ',group_concat(concat(`ds_renderer_stylesheet_attributes`.`attribute`,': ',`ds_renderer_stylesheet_attributes`.`values`) separator '; '),'}') AS `css` from (`ds_renderer_stylesheet` left join `ds_renderer_stylesheet_attributes` on(`ds_renderer_stylesheet`.`classname` = `ds_renderer_stylesheet_attributes`.`classname`)) group by `ds_renderer_stylesheet`.`group`,`ds_renderer_stylesheet`.`classname` //
CREATE OR REPLACE VIEW `view_readtable_ds_renderer_stylesheet_groups` AS select `ds_renderer_stylesheet_groups`.`id` AS `id`,`ds_renderer_stylesheet_groups`.`name` AS `name`,group_concat(`view_readtable_ds_renderer_stylesheet`.`css` order by `view_readtable_ds_renderer_stylesheet`.`classname` ASC separator ' ') AS `css` from (`ds_renderer_stylesheet_groups` left join `view_readtable_ds_renderer_stylesheet` on(`view_readtable_ds_renderer_stylesheet`.`group` = `ds_renderer_stylesheet_groups`.`id`)) group by `ds_renderer_stylesheet_groups`.`id`,`ds_renderer_stylesheet_groups`.`name` //
CREATE OR REPLACE VIEW `view_readtable_ds_renderer_stylesheet_groups_assign` AS select `ds_pug_templates`.`id` AS `pug_id`,`ds_renderer_stylesheet_groups`.`id` AS `group_id`,ifnull(`ds_renderer_stylesheet_groups_assign`.`active`,0) AS `active`,ifnull(`view_readtable_ds_renderer_stylesheet_groups`.`css`,'') AS `css` from (((`ds_renderer_stylesheet_groups` join `ds_pug_templates`) left join `ds_renderer_stylesheet_groups_assign` on(`ds_pug_templates`.`id` = `ds_renderer_stylesheet_groups_assign`.`pug_id` and `ds_renderer_stylesheet_groups`.`id` = `ds_renderer_stylesheet_groups_assign`.`group_id`)) left join `view_readtable_ds_renderer_stylesheet_groups` on(`ds_renderer_stylesheet_groups`.`id` = `view_readtable_ds_renderer_stylesheet_groups`.`id`)) //


